preup() { # Test for link on the interface prior to bringing it up. This # only works on some network adapters and requires the mii-diag # package to be installed. if mii-tool ${IFACE} 2> /dev/null | grep -q 'no link'; then ewarn "No link on ${IFACE}, aborting configuration" return 1 fi
# Test for link on the interface prior to bringing it up. This # only works on some network adapters and requires the ethtool # package to be installed. if ethtool ${IFACE} | grep -q 'Link detected: no'; then ewarn "No link on ${IFACE}, aborting configuration" return 1 fi
# Remember to return 0 on success return 0 }
predown() { # The default in the script is to test for NFS root and disallow # downing interfaces in that case. Note that if you specify a # predown() function you will override that logic. Here it is, in # case you still want it... if is_net_fs /; then eerror "root filesystem is network mounted -- can't stop ${IFACE}" return 1 fi
# Remember to return 0 on success return 0 }
postup() { # This function could be used, for example, to register with a # dynamic DNS service. Another possibility would be to # send/receive mail once the interface is brought up. return 0 }
postdown() { # This function is mostly here for completeness... I haven't # thought of anything nifty to do with it yet ;-) return 0 }

preassociate() { # The below adds two configuration variables leap_user_ESSID # and leap_pass_ESSID. When they are both configured for the ESSID # being connected to then we run the CISCO LEAP script
local user pass eval user=\"\$\{leap_user_${ESSIDVAR}\}\" eval pass=\"\$\{leap_pass_${ESSIDVAR}\}\"
if [[ -n ${user} && -n ${pass} ]]; then if [[ ! -x /opt/cisco/bin/leapscript ]]; then eend "For LEAP support, please emerge net-misc/cisco-aironet-client-utils" return 1 fi einfo "Waiting for LEAP Authentication on \"${ESSID//\\\\//}\"" if /opt/cisco/bin/leapscript ${user} ${pass} | grep -q 'Login incorrect'; then ewarn "Login Failed for ${user}" return 1 fi fi
return 0 }
postassociate() { # This function is mostly here for completeness... I haven't # thought of anything nifty to do with it yet ;-)
return 0 }
