#!/usr/bin/env awk -f
BEGIN {
    FS="\t+"
    months="Ene,Feb,Mar,Abr,May,Jun,Jul,Ago,Sep,Oct,Nov,Dic"
}

function monthdigit(mymonth) {
    return (index(months,mymonth)+3)/4
}

function doincome(mybalance) {
    mybalance[curmonth,$3] += amount
    mybalance[0,$3] += amount
}

function doexpense(mybalance) {
    mybalance[curmonth,$2] -= amount
    mybalance[0,$2] -= amount
}

function dotransfer(mybalance) {
    mybalance[0,$2] -= amount
    mybalance[curmonth,$2] -= amount
    mybalance[0,$3] += amount
    mybalance[curmonth,$3] += amount
}

{
    curmonth=monthdigit(substr($1,4,3))
    amount=$7

    #registra todas las categorías que encuentra
    if ( $2 != "-" )
        globcat[$2]="yes"
    if ( $3 != "-" )
        globcat[$3]="yes"

    #tally up the transaction properly
    if ( $2 == "-" ) {
        if ( $3 == "-" ) {
            print "Error: ¡Ambos campos inc y exp están vacíos!"
            exit 1
        } else {
            #esto es un ingreso
            doincome(balance)
            if ( $5 == "Y" )
                doincome(balance2)
        }
    } else if ( $3 == "-" ) {
        #esto es un gasto
        doexpense(balance)
        if ( $5 == "Y" )
            doexpense(balance2)
    } else {
        #esto es una transferencia
        dotransfer(balance)
        if ( $5 == "Y" )
            dotransfer(balance2)
    }
}

END {
    bal=0
    bal2=0
    for (x in globcat) {
        bal=bal+balance[0,x]
        bal2=bal2+balance2[0,x]
    }
    printf("Tus fondos actuales: %10.2f\n", bal)
    printf("El saldo de tu cuenta: %10.2f\n", bal2)
}
