preup() { # Testataan linkkiä ennen nostamista. Tämä toimii vain # joillakin laitteilla ja vaatii mii-diagin asennettuna. if mii-tool ${IFACE} 2> /dev/null | grep -q 'no link'; then ewarn "No link on ${IFACE}, aborting configuration" return 1 fi
# Testataan linkin rajapintaa ennen nostamista. # Tämä toimii vain joillain laitteilla ja vaatii ethtoolin asennettuna
if ethtool ${IFACE} | grep -q 'Link detected: no'; then ewarn "No link on ${IFACE}, aborting configuration" return 1 fi
# Muista return 0 onnistuttua return 0 }
predown() { # oletuksena skripti testaa NFS-juuren ja kieltää verkkorajapinnan # tiputuksen tapauksittain. Jos predown() määritellään itse, oletusarvo # häipyy, joten tässä on sama funktio jos kuitenkin se halutaan. if is_net_fs /; then eerror "root filesystem is network mounted -- can't stop ${IFACE}" return 1 fi
# Muista return 0 onnistuttua return 0 }
postup() { # Tätä voisi käyttää vaikka dyndns-palvelun kanssa. Toinen # vaihtoehto on vaikkapa postin toimitus kun rajapinta on ylhäällä
return 0 }
postdown() { # Tämä funktio on täällä kai symmetrian vuoksi, kehittäjä itse # ei ainakaan keksi mitään hyötykäyttöä ;-) return 0 }

preassociate() { # Allaoleva lisää muuttujat leap_user_ESSID ja leap_pass_ESSID. # kun ne on asetettu yhdistettyyn ESSID:hen voidaan ajaa CISCOn LEAP-skripti
local user pass eval user=\"\$\{leap_user_${ESSIDVAR}\}\" eval pass=\"\$\{leap_pass_${ESSIDVAR}\}\"
if [[ -n ${user} && -n ${pass} ]]; then if [[ ! -x /opt/cisco/bin/leapscript ]]; then eend "For LEAP support, please emerge net-misc/cisco-aironet-client-utils" return 1 fi einfo "Waiting for LEAP Authentication on \"${ESSID//\\\\//}\"" if /opt/cisco/bin/leapscript ${user} ${pass} | grep -q 'Login incorrect'; then ewarn "Login Failed for ${user}" return 1 fi fi
return 0 }
postassociate() { # Tämä funktio on täällä vain symmetrian takia ;-) return 0 }
