body.pre:
if (getcwd() =~ 'gentoo-x86\|gentoo-src\|portage')
	set tabstop=4 shiftwidth=4 noexpandtab
endif

DEPEND="gnome? ( >=gnome-base/gnome-1.4 ) mysql? ( >=dev-db/mysql-3.23.49 )"
src_compile() { local myconf use gnome || myconf="--disable-gnome" use mysql || myconf="${myconf} --disable-mysql"
./configure ${myconf} --prefix=/usr --host=${CHOST} || die emake || die }

DEPEND="gnome? ( >=gnome-base/gnome-1.4 ) mysql? ( >=dev-db/mysql-3.23.49 )"
src_compile() { local myconf use gnome && myconf="--enable-gnome" use mysql && myconf="${myconf} --enable-mysql"
./configure ${myconf} --prefix=/usr --host=${CHOST} || die emake || die }
