CREATE TABLE users ( id int unsigned NOT NULL auto_increment, priority int NOT NULL DEFAULT '7', -- 0 est une priorité faible policy_id int unsigned NOT NULL DEFAULT '1', email varchar(255) NOT NULL, fullname varchar(255) DEFAULT NULL, -- non utilisé par amavisd-new local char(1), -- Y/N (champ optionnel, voir la note plus bas) PRIMARY KEY (id), KEY email (email) ); CREATE UNIQUE INDEX users_idx_email ON users(email);
-- (Toute adresse courrier, externe ou locale, utilisée comme expéditeur dans wblist) CREATE TABLE mailaddr ( id int unsigned NOT NULL auto_increment, priority int NOT NULL DEFAULT '7', -- 0 est une priorité faible email varchar(255) NOT NULL, PRIMARY KEY (id), KEY email (email) ); CREATE UNIQUE INDEX mailaddr_idx_email ON mailaddr(email);
-- (listes blanches et/ou noires par destinataires, -- mettre les expéditeurs et destinataires à la bonne place) (expéditeurs sur listes blanches ou noires) CREATE TABLE wblist ( rid int unsigned NOT NULL, -- destinataire : users.id sid int unsigned NOT NULL, -- expéditeur : mailaddr.id wb char(1) NOT NULL, -- W/Y (blanche), B/N (noir), espace=neutre PRIMARY KEY (rid,sid) );
CREATE TABLE policy ( id int unsigned NOT NULL auto_increment, policy_name varchar(32), -- non utilisé par amavisd-new virus_lover char(1), -- Y/N (Oui/Non) spam_lover char(1), -- Y/N (champ optionnel) banned_files_lover char(1), -- Y/N (champ optionnel) bad_header_lover char(1), -- Y/N (champ optionnel) bypass_virus_checks char(1), -- Y/N bypass_spam_checks char(1), -- Y/N bypass_banned_checks char(1), -- Y/N (champ optionnel) bypass_header_checks char(1), -- Y/N (champ optionnel) spam_modifies_subj char(1), -- Y/N (champ optionnel) spam_quarantine_to varchar(64) DEFAULT NULL, -- (champ optionnel) spam_tag_level float, -- un score supérieur ajoute les en-têtes de spam spam_tag2_level float DEFAULT NULL, -- un score supérieur ajoute les champs -- d'en-tête d'information 'déclaré comme spam' spam_kill_level float, -- un score supérieur active des actions d'évasion -- comme rejet/oubli, quarantaine, ... -- (lié à la configuration de final_spam_destiny) PRIMARY KEY (id) );

