mysql> delete from user where not (host="localhost" and user="root");
mysql> flush privileges;

defaults { only_from = localhost instances = 10 log_type = SYSLOG authpriv info log_on_success = HOST PID log_on_failure = HOST cps = 25 30 }
# Ini akan men-setup pserver (cvs) lewat xinetd dengan setting-setting berikut: # maks 10 ( 10 koneksi pada satu waktu) # membatasi pserver hanya ke tcp # gunakan user cvs untuk menjalankan servis ini # ikat antarmuka jaringan ke satu IP saja # izinkan akses dari 10.0.0.* # batasi waktu yang dapat digunakan pengembang untuk menggunakan cvs # dari pukul 08 sampai pukul 17 # gunakan tcp wrapper (akses dikendalikan di # /etc/hosts.allow dan /etc/hosts.deny) # max_load di komputer di-set ke 1.0 # flag yang dinonaktifkan secara default di-set ke "no" tetapi penulis # tetap ingin memilikinya jika memang harus dinonaktifkan
service cvspserver { socket_type = stream protocol = tcp instances = 10 protocol = tcp wait = no user = cvs bind = 10.0.0.2 only_from = 10.0.0.0 access_times = 8:00-17:00 server = /usr/sbin/tcpd server_args = /usr/bin/cvs --allow-root=/mnt/cvsdisk/cvsroot pserver max_load = 1.0 log_on_failure += RECORD disable = no }
