#!/usr/bin/env bash
if [ $# -ne 2 ] then echo "Please specify two args - .ebuild file and unpack, compile or all" exit 1 fi
if [ -z "$DISTDIR" ] then # impostate DISTDIR a /usr/src/distfiles se non lo avete già fatto DISTDIR=/usr/src/distfiles fi export DISTDIR
ebuild_unpack() { #assicuratevi di essere nella giusta directory cd ${ORIGDIR}
if [ -d ${WORKDIR} ] then rm -rf ${WORKDIR} fi
mkdir ${WORKDIR} cd ${WORKDIR} if [ ! -e ${DISTDIR}/${A} ] then echo "${DISTDIR}/${A} does not exist. Please download first." exit 1 fi tar xzf ${DISTDIR}/${A} echo "Unpacked ${DISTDIR}/${A}." #source is now correctly unpacked }
ebuild_compile() {
#assicuratevi di essere nella giusta directory cd ${SRCDIR} if [ ! -d "${SRCDIR}" ] then echo "${SRCDIR} does not exist -- please unpack first." exit 1 fi ./configure --prefix=/usr make }
export ORIGDIR=`pwd` export WORKDIR=${ORIGDIR}/work
if [ -e "$1" ] then source $1 else echo "Ebuild file $1 not found." exit 1 fi
export SRCDIR=${WORKDIR}/${P}
case "${2}" in unpack) ebuild_unpack ;; compile) ebuild_compile ;; all) ebuild_unpack ebuild_compile ;; *) echo "Please specify unpack, compile or all as the second arg" exit 1 ;; esac

ebuild_compile() {
        #assicuratevi di essere nella giusta directory
        if [ ! -d "${SRCDIR}" ]
        then
                ebuild_unpack
        fi
        cd ${SRCDIR}
        ./configure --prefix=/usr
        make     
}

user_compile() {
        #siamo già in ${SRCDIR}
        if [ -e configure ]
        then
                #run configure script if it exists
                ./configure --prefix=/usr
        fi
        #run make
        make
}              

ebuild_compile() {
        if [ ! -d "${SRCDIR}" ]
        then
                echo "${SRCDIR} does not exist -- please unpack first."
                exit 1
        fi
        #assicuratevi di essere nella giusta directory
        cd ${SRCDIR}
        user_compile
}

#!/usr/bin/env bash
if [ $# -ne 2 ] then echo "Please specify ebuild file and unpack, compile or all" exit 1 fi
source /etc/ebuild.conf
if [ -z "$DISTDIR" ] then # impostare DISTDIR a /usr/src/distfiles se non è stato già fatto DISTDIR=/usr/src/distfiles fi export DISTDIR
ebuild_unpack() { #assicuratevi di essere nella giusta directory cd ${ORIGDIR}
if [ -d ${WORKDIR} ] then rm -rf ${WORKDIR} fi
mkdir ${WORKDIR} cd ${WORKDIR} if [ ! -e ${DISTDIR}/${A} ] then echo "${DISTDIR}/${A} does not exist. Please download first." exit 1 fi tar xzf ${DISTDIR}/${A} echo "Unpacked ${DISTDIR}/${A}." #ora i sorgenti sono stati estratti correttamente }
user_compile() { #siamo già in ${SRCDIR} if [ -e configure ] then #eseguire lo script configure se esiste ./configure --prefix=/usr fi #eseguire make make $MAKEOPTS MAKE="make $MAKEOPTS" }
ebuild_compile() { if [ ! -d "${SRCDIR}" ] then echo "${SRCDIR} does not exist -- please unpack first." exit 1 fi #assicuratevi di essere nella giusta directory cd ${SRCDIR} user_compile }
export ORIGDIR=`pwd` export WORKDIR=${ORIGDIR}/work
if [ -e "$1" ] then source $1 else echo "Ebuild file $1 not found." exit 1 fi
export SRCDIR=${WORKDIR}/${P}
case "${2}" in unpack) ebuild_unpack ;; compile) ebuild_compile ;; all) ebuild_unpack ebuild_compile ;; *) echo "Please specify unpack, compile or all as the second arg" exit 1 ;; esac
