EXPORT_FUNCTIONS() {
	while [ "$1" ]; do
		eval "$1() { ${ECLASS}_$1 ; }" > /dev/null
		shift
	done
}

#in foo.eclass:
foo_src_compile() {
	[default code here]
}

EXPORT_FUNCTIONS src_compile
#end eclass code

#in an ebuild:
inherit foo

src_compile() {
	[codice personalizzato]
	foo_src_compile
	[ancora codice personalizzato]
}

base_src_compile() {

    [ -z "$1" ] && base_src_compile all

    while [ "$1" ]; do
        case $1 in
            configure)
                ./configure || die;;
            make)
                emake || die;;
            all)
                base_src_compile configure make;;
        esac
    shift
    done
}

base_src_compile() {
debug-print function $FUNCNAME $* [ -z "$1" ] && base_src_compile all
while [ "$1" ]; do case $1 in configure) debug-print-section configure ./configure || die;; make) debug-print-section make make || die;; all) debug-print-section all base_src_compile configure make;; esac shift done
debug-print "$FUNCNAME: result is $RESULT" }
