mysql> create database maildb;
mysql> GRANT INSERT,UPDATE,DELETE,SELECT ON maildb.* TO 'mail'@'localhost' IDENTIFIED BY 'password_segretissima';
mysql> use maildb;

CREATE TABLE users ( id int unsigned NOT NULL auto_increment, priority int NOT NULL DEFAULT '7', -- 0 è bassa priorità policy_id int unsigned NOT NULL DEFAULT '1', email varchar(255) NOT NULL, fullname varchar(255) DEFAULT NULL, -- non usato da amavisd-new local char(1), -- Y/N (campo opzionale, vedere note) PRIMARY KEY (id), KEY email (email) ); CREATE UNIQUE INDEX users_idx_email ON users(email);
(-- ogni indirizzo e-mail, esterno o locale, usato come mittente in 'wblist') CREATE TABLE mailaddr ( id int unsigned NOT NULL auto_increment, priority int NOT NULL DEFAULT '7', -- 0 è bassa priorità email varchar(255) NOT NULL, PRIMARY KEY (id), KEY email (email) ); CREATE UNIQUE INDEX mailaddr_idx_email ON mailaddr(email);
(-- whitelist per destinatario e/o blacklist, -- mette in relazione mittente e destinatario, 'wb') (white or blacklisted sender) CREATE TABLE wblist ( rid int unsigned NOT NULL, -- destinatario: users.id sid int unsigned NOT NULL, -- mittente: mailaddr.id wb char(1) NOT NULL, -- W or Y / B or N / space=neutral PRIMARY KEY (rid,sid) );
CREATE TABLE policy ( id int unsigned NOT NULL auto_increment, policy_name varchar(32), -- non usato da amavisd-new virus_lover char(1), -- Y/N spam_lover char(1), -- Y/N (campo opzionale) banned_files_lover char(1), -- Y/N (campo opzionale) bad_header_lover char(1), -- Y/N (campo opzionale) bypass_virus_checks char(1), -- Y/N bypass_spam_checks char(1), -- Y/N bypass_banned_checks char(1), -- Y/N (campo opzionale) bypass_header_checks char(1), -- Y/N (campo opzionale) spam_modifies_subj char(1), -- Y/N (campo opzionale) spam_quarantine_to varchar(64) DEFAULT NULL, -- (campo opzionale) spam_tag_level float, -- un punteggio alto inserisce le intestazioni spam spam_tag2_level float DEFAULT NULL, -- un punteggio alto inserisce le -- intestazioni di 'spam dichiarato' spam_kill_level float, -- un punteggio alto attiva le azioni evasive: -- rifiuto/scarto, quarantena, ... -- (soggetto all'impostazione di final_spam_destiny) PRIMARY KEY (id) );

@lookup_sql_dsn =
   ( ['DBI:mysql:maildb:host1', 'mail', 'password_segretissima']  );

(Per chiarezza deommentare l'impostazione predefinita)
$sql_select_policy = 'SELECT *,users.id FROM users,policy'.
   ' WHERE (users.policy_id=policy.id) AND (users.email IN (%k))'.
   ' ORDER BY users.priority DESC';

(Per ottenere white/blacklisting in base al mittente)
   $sql_select_white_black_list = 'SELECT wb FROM wblist,mailaddr'.
     ' WHERE (wblist.rid=?) AND (wblist.sid=mailaddr.id)'.
     '   AND (mailaddr.email IN (%k))'.
     ' ORDER BY mailaddr.priority DESC';

