preup() { # Probeer eerst een verbinding te maken voor deze wordt vastgelegd. # Dit werkt niet voor alle adapters en heeft het pakket mii-diag nodig # om gebruikt te kunnen worden. if mii-tool ${IFACE} 2> /dev/null | grep -q 'no link'; then ewarn "No link on ${IFACE}, aborting configuration" return 1 fi
# Probeer eerst een verbinding te maken voor deze wordt vastgelegd. # Dit werkt niet voor alle adapters en heeft het pakket ethtool nodig om # gebruikt te kunnen worden. if ethtool ${IFACE} | grep -q 'Link detected: no'; then ewarn "No link on ${IFACE}, aborting configuration" return 1 fi
# Vergeet niet om een 0 als resultaat door te sturen indien succesvol return 0 }
predown() { # Standaard wordt getest of de root op een NFS bestandssysteem staat en # mogen de interfaces niet stoppen als dat zo is. Wanneer u de functie # predown() instelt is deze standaard overschreven. Hier is de code, voor # als u die nog steeds wilt gebruiken... if is_net_fs /; then eerror "root filesystem is network mounted -- can't stop ${IFACE}" return 1 fi
# Vergeet niet om een 0 als resultaat door te sturen indien succesvol return 0 }
postup() { # Deze functie kunt u onder ander gebruiken om contact te maken met een # dynamic DNS service. Een ander mogelijkheid zou zijn om email te # verzenden/ontvangen zodra de interface is gestart. return 0 }
postdown() { # Deze functie staat hier om de handleiding compleet te houden, maar # we hebben er nog geen mogelijkheden bij bedacht ;-) return 0 }

preassociate() { # Het onderstaande voorbeeld voegt twee variabelen toe, leap_user_ESSID # en leap_pass_ESSID. Als deze zijn ingesteld voor uw ESSID, wordt het script # CISCO LEAP gedraaid.
local user pass eval user=\"\$\{leap_user_${ESSIDVAR}\}\" eval pass=\"\$\{leap_pass_${ESSIDVAR}\}\"
if [[ -n ${user} && -n ${pass} ]]; then if [[ ! -x /opt/cisco/bin/leapscript ]]; then eend "For LEAP support, please emerge net-misc/cisco-aironet-client-utils" return 1 fi einfo "Waiting for LEAP Authentication on \"${ESSID//\\\\//}\"" if /opt/cisco/bin/leapscript ${user} ${pass} | grep -q 'Login incorrect'; then ewarn "Login Failed for ${user}" return 1 fi fi
return 0 }
postassociate() { # Deze functie staat hier om de handleiding compleet te houden, maar # we hebben er nog geen mogelijkheden bij bedacht ;-) return 0 }
