#!/usr/bin/env bash
for x in jeden dwa trzy cztery do echo numer $x done
Wyjście skryptu: numer jeden numer dwa numer trzy numer cztery

case "${x##*.}" in
     gz)
           gzunpack ${SROOT}/${x}
           ;;
     bz2)
           bz2unpack ${SROOT}/${x}
           ;;
     *)
           echo "Format archiwum nie został rozpoznany."
           exit
           ;;
esac

#!/usr/bin/env bash
myvar="cześć"
myfunc() {
myvar="raz dwa trzy" for x in $myvar do echo $x done }
myfunc
echo $myvar $x

#!/usr/bin/env bash
myvar="cześć"
myfunc() { local x local myvar="raz dwa trzy" for x in $myvar do echo $x done }
myfunc
echo $myvar $x
