#include <pthread.h> 
#include <stdlib.h> 
#include <unistd.h>

void *MojaFunkcjaDlaWatku(void *arg) { 
        int i; 
        for ( i=0; i<20; i++ ) {
                printf("Watek mowi czesc!\n"); 
                sleep(1); 
        } 
        return NULL; 
}

int main(void) {

  pthread_t mojwatek;

  if ( pthread_create( &mojwatek, NULL, MojaFunkcjaDlaWatku, NULL) ) {
        printf("blad przy tworzeniu watku\n"); abort(); 
  }

  if ( pthread_join ( mythread, NULL ) ) { 
        printf("blad w konczeniu watku\n");
        abort(); 
  }

  exit(0);

}


#include <pthread.h> 
#include <stdlib.h>
#include <unistd.h> 
#include <stdio.h>

int mojazmiennaglobalna=0;

void *MojaFunkcjaDlaWatku(void *arg) { 
        int i,j; 
        for ( i=0; i<20; i++ ) {
                j=mojazmiennaglobalna; 
                j=j+1; 
                printf("."); 
                fflush(stdout); 
                sleep(1);
                mojazmiennaglobalna=j; 
        } 
        return NULL; 
}

int main(void) {

  pthread_t mojwatek; 
  int i;

  if ( pthread_create( &mojwatek, NULL, MojaFunkcjaDlaWatku, NULL) ) {
        printf("blad przy tworzeniu watku."); 
        abort(); 
  }

  for ( i=0; i<20; i++) { 
        mojazmiennaglobalna=mojazmiennaglobalna+1;
        printf("o"); 
        fflush(stdout); 
        sleep(1); 
  }

  if ( pthread_join ( mojwatek, NULL ) ) { 
        printf("blad przy konczeniu watku."); 
        abort(); 
  }

  printf("\nMoja zmienna globalna wynosi %d\n",mojazmiennaglobalna);

  exit(0);

}
