preup() { # Sprawdza czy istnieje link na interfejsie, który jest # uruchamiany. Ta opcja działa jedynie na kilku urządzeniach # sieciowych i wymaga, aby pakiet mii-diag był zainstalowany if mii-tool ${IFACE} 2> /dev/null | grep -q 'no link'; then ewarn "Nie ma linku na ${IFACE}, przerywam konfigurację" return 1 fi
# Sprawdza czy istnieje link na interfejsie przed jego # uruchomieniem. Ta opcja działa tylko na niektórych urządzeniach # sieciowych i wymaga, aby pakiet ethtool był zainstalowany if ethtool ${IFACE} | grep -q 'Link detected: no'; then ewarn "Brakuje linku na ${IFACE}, przerywam konfigurację" return 1 fi
# Należy pamiętać o zwróceniu wartości 0 w przypadku powodzenia return 0 }
predown() { # Domyślny skrypt sprawdza czy istnieje katalog główny NFS i # nie zezwala na wyłączenie interfejsów w takim przypadku. Należy # zauważyć, że jeśli określi się własną funkcję predown(), to właściwość # ta zostanie usunięta. Poniżej znajduje się ten skrypt, jeśli okazałoby # się, że jest nadal potrzebny... if is_net_fs /; then eerror "katalog główny jest zamontowany przez sieć -- nie można zatrzymać ${IFACE}" return 1 fi
# Należy pamiętać o zwróceniu wartości 0 w przypadku powodzenia return 0 }
postup() { # Ta funkcja zostanie użyta dla przykładu, aby zarejestrować # interfejs z usługą DNS. Inną możliwością jest np. wysłanie wiadomości # mailowej z informacją o uruchomieniu interfejsu return 0 }
postdown() { # Ta funkcja jest tu tylko dla zasady... Jeszcze nie # wiadomo co fajnego można z nią zrobić ;-) return 0 }

preassociate() { # Poniższe linijki dodają dwie zmienne leap_user_ESSID oraz # leap_pass_ESSID. Jeżeli obie są skonfigurowane dla ESSID-a z którym są # połączone, uruchamiany jest skrypt CISCO LEAP
local user pass eval user=\"\$\{leap_user_${ESSIDVAR}\}\" eval pass=\"\$\{leap_pass_${ESSIDVAR}\}\"
if [[ -n ${user} && -n ${pass} ]]; then if [[ ! -x /opt/cisco/bin/leapscript ]]; then eend "For LEAP support, please emerge net-misc/cisco-aironet-client-utils" return 1 fi einfo "Waiting for LEAP Authentication on \"${ESSID//\\\\//}\"" if /opt/cisco/bin/leapscript ${user} ${pass} | grep -q 'Login incorrect'; then ewarn "Login Failed for ${user}" return 1 fi fi
return 0 }
postassociate() { # Ta funkcja jest tu tylko dla zasady... Jeszcze nie # wiadomo co fajnego można z nią zrobić ;-)
return 0 }
