preup() { # Проверка соединения интерфейса перед его запуском. Она # работает лишь с некоторыми сетевыми адаптерами и требует наличия # установленного пакета mii-diag. if mii-tool ${IFACE} 2> /dev/null | grep -q 'no link'; then ewarn "Интерфейс ${IFACE} не подключен, прерывание запуска" return 1 fi
# Проверка соединения интерфейса перед его запуском. Она # работает лишь с некоторыми сетевыми адаптерами и требует наличия # установленного пакета ethtool. if ethtool ${IFACE} | grep -q 'Link detected: no'; then ewarn "Интерфейс ${IFACE} не подключен, прерывание запуска" return 1 fi
# Не забываем вернуть 0 при успехе return 0 }
predown() { # Назначение этого сценария - проверить наличие корня NFS # и в этом случае предотвратить останов интерфейсов. Заметьте, что # определяя функцию predown(), вы отменяете существующую логику. # Вот она, на случай если все же понадобится... if is_net_fs /; then eerror "Корневая ФС смонтирована в сети - останов ${IFACE} невозможен" return 1 fi
# Не забываем вернуть 0 при успехе return 0 }
postup() { # Эту функцию можно использовать, например, для регистрации в # службе динамического DNS. Другой пример - отправка/прием почты после # запуска интерфейса. return 0 }
postdown() { # Эта функция приводится в основном для полноты... Я не придумал, # что бы ценное в нее поместить ;-) return 0 }

preassociate() { # Ниже добавляются две конфигурационных переменных, leap_user_ESSID # и leap_pass_ESSID. Когда они обе настроены на подключаемый ESSID, # мы запускаем сценарий CISCO LEAP
local user pass eval user=\"\$\{leap_user_${ESSIDVAR}\}\" eval pass=\"\$\{leap_pass_${ESSIDVAR}\}\"
if [[ -n ${user} && -n ${pass} ]]; then if [[ ! -x /opt/cisco/bin/leapscript ]]; then eend "Для поддержки LEAP, выполните emerge net-misc/cisco-aironet-client-utils" return 1 fi einfo "Ожидание допуска LEAP на \"${ESSID//\\\\//}\"" if /opt/cisco/bin/leapscript ${user} ${pass} | grep -q 'Login incorrect'; then ewarn "Вход пользователя ${user} не удался" return 1 fi fi
return 0 }
postassociate() { # Эта функция приводится в основном для полноты... Я не придумал, # что бы ценное в нее поместить ;-)
return 0 }
