# следующую строку нельзя изменять, иначе программа не сможет работать ctrl_interface=/var/run/wpa_supplicant
# ограничим доступ к настройкам WPA только для root ctrl_interface_group=0
# пусть wpa_supplicant заботится о сканировании и выборе точки доступа ap_scan=1
# простой случай: WPA-PSK, согласованный ключ - текстовая строка, # принимать любой допустимый шифр network={ ssid="просто" psk="очень тайный пароль" # чем выше приоритет, тем скорее выбор priority=5 }
# как в предыдущем, но с запросом сканирования по определенному SSID # (для точек доступа, отклоняющих широковешательный SSID) network={ ssid="второй ssid" scan_ssid=1 psk="очень тайный пароль" priority=2 }
# использовать только WPA-PSK; принимать любое допустимое сочетание шифров
network={ ssid="пример" proto=WPA key_mgmt=WPA-PSK pairwise=CCMP TKIP group=CCMP TKIP WEP104 WEP40 psk=06b4be19da289f475aa46a33cb793029d4ab3db7a23ee92382eb0106c72ac7bb priority=2 }
# открытое подключение без шифрования (не WPA, не IEEE #802.1X)
network={ ssid="тест-открытого-текста" key_mgmt=NONE }
# подключение с общим ключом WEP (не WPA, не IEEE #802.1X) network={ ssid="тест-статического-wep" key_mgmt=NONE wep_key0="abcde" wep_key1=0102030405 wep_key2="1234567890123" wep_tx_keyidx=0 priority=5 }
# подключение с общим ключом WEP (не WPA, не IEEE #802.1X), # допуск c использованим общего ключа IEEE 802.11 network={ ssid="тест2-статического-wep" key_mgmt=NONE wep_key0="abcde" wep_key1=0102030405 wep_key2="1234567890123" wep_tx_keyidx=0 priority=5 auth_alg=SHARED }
# сеть IBSS/ad-hoc с WPA-None/TKIP network={ ssid="тест adhoc" mode=1 proto=WPA key_mgmt=WPA-NONE pairwise=NONE group=TKIP psk="тайный пароль" }
