#!/bin/sh
set $*
group=${1/\/*/} action=${1/*\//}
# AC 模式下的執行階層 RLVL_AC="default" # 電池模式下的執行階層 RLVL_BATTERY="battery"
# 指示 AC 狀態的檔案，使用前請先確定檔案名稱 AC_STATE="/proc/acpi/ac_adapter/AC/state" # 此行表示在 AC 模式 AC_ON="on-line" # 此行表示在電池模式 AC_OFF="off-line"
function SwitchRunlevel() { if [[ "$(grep ${AC_OFF} ${AC_STATE})" != "" && "$(cat /var/lib/init.d/softlevel)" != "${RLVL_BATTERY}" ]] then logger "Switching to ${RLVL_BATTERY} runlevel" /sbin/rc ${RLVL_BATTERY} elif [[ "$(grep ${AC_ON} ${AC_STATE})" != "" && "$(cat /var/lib/init.d/softlevel)" != "${RLVL_AC}" ]] then logger "Switching to ${RLVL_AC} runlevel" /sbin/rc ${RLVL_AC} fi }
case "$group" in battery) case "$action" in battery) SwitchRunlevel ;; *) logger "ACPI group battery / action $action is not defined" ;; esac ;;
ac_adapter) case "$action" in ac_adapter) SwitchRunlevel ;; *) logger "ACPI group ac_adapter / action $action is not defined" ;; esac ;; *) logger "ACPI group $group / action $action is not defined" ;; esac

#!/sbin/runscript
start() {
  ebegin "Activating Power Management for Hard Drives"
  hdparm -q -S12 /dev/hda
  eend $?
}

stop () {
  ebegin "Deactivating Power Management for Hard Drives"
  hdparm -q -S253 /dev/hda
  eend $?
}

#!/sbin/runscript
start() {
  ebegin "Activating Power Management for Wireless LAN"
  iwconfig wlan0 power on power max period 3
  eend $?
}

stop () {
  ebegin "Deactivating Power Management for Wireless LAN"
  iwconfig wlan0 power off
  eend $?
}
