# Zuerst binden wir das libconf Vorlagenmodul ein
use Libconf::Glueconf::X::Gdm;
# Dann benutzen wir diese Vorlage
my $gdm_conf = Libconf::Glueconf::X::Gdm->new({ filename =>
'/etc/X11/gdm/gdm.conf' });
