#!/bin/sh #-------- # Поиск синонимов в командной строке
BROWSER="/usr/bin/lynx -source" WEBSITE="http://thesaurus.reference.com/search?q=$1" HTML2TEXT="/usr/bin/html2text -style compact"
if test $1; then ${BROWSER} ${WEBSITE} | ${HTML2TEXT} | ${PAGER} else echo "Usage: $0 word" exit 1 fi
