body.pre:
inherit eutils java-pkg
DESCRIPTION="Fictional example ebuild." HOMEPAGE="http://www.gentoo.org/" SRC_URI="mirror://gentoo/${P}-src.tar.gz"
LICENSE="Apache-2.0" SLOT="0" KEYWORDS="~x86 ~sparc ~ppc ~amd64 ppc64" IUSE="doc examples source"
RDEPEND=">=virtual/jre-1.3 =dev-java/xerces-2* >=dev-java/log4j-1.2.8" DEPEND=">=virtual/jdk-1.3 ${RDEPEND} >=dev-java/ant-core-1.5.4 source? ( app-arch/zip )"
S=${WORKDIR}/${P}-src
src_unpack() { unpack ${A}
cd ${S}/lib rm *.jar
java-pkg_jar-from xerces-2 java-pkg_jar-from log4j log4j.jar log4j-1.2.8.jar }
src_compile() { local antflags="jar" use doc && antflags="${antflags} doc" ant ${antflags} || die "failed to build" }
java_src_install() { java-pkg_newjar target/${P}-dev.jar ${PN}.jar
use doc && java-pkg_dohtml -r dist/docs/ use source && java-pkg_dosrc src/java/org if use examples; then dodir /usr/share/doc/${PF}/examples cp -r src/java/examples/* ${D}/usr/share/doc/${PF}/examples fi }
