DEPEND="X? ( >=x11-base/xfree-4.3 ) mysql? ( >=dev-db/mysql-3.23.49 ) apache2? ( >=net-www/apache-2 ) !apache2? ( =net-www/apache-1* )"
src_compile() { econf \ $(use_enable X x11) \ $(use_enable mysql)
emake || die "Error: emake failed!" }

src_compile() { local myconf
if use gnutls ; then myconf="${myconf} --enable-ssl --with-ssl=gnutls" elif use ssl ; then myconf="${myconf} --enable-ssl --with-ssl=openssl" else myconf="${myconf} --disable-ssl" fi
econf \ # Other stuff ${myconf}
emake || die "make failed" }
