EXPORT_FUNCTIONS() {
    while [ "$1" ]; do
        eval "$1() { ${ECLASS}_$1 ; }" > /dev/null
        shift
    done
}

#in foo.eclass:
foo_src_compile() {
	[qui codice predefinito]
}

EXPORT_FUNCTIONS src_compile

#fine codice eclass

#in un ebuild:

inherit foo

src_compile() {
	[qui codice personalizzato]
	foo_src_compile
	[altro codice personalizzato]
}

base_src_compile() {

    [ -z "$1" ] && base_src_compile all

    while [ "$1" ]; do
        case $1 in
            configure)
                ./configure || die;;
            make)
                emake || die;;
            all)
                base_src_compile configure make;;
        esac
    shift
    done

}

base_src_compile() {
debug-print function [ -z "$1" ] && base_src_compile all
while [ "$1" ]; do case $1 in configure) debug-print-section configure ./configure || die;; make) debug-print-section make make || die;; all) debug-print-section all base_src_compile configure make;; esac shift done
debug-print "${FUNCNAME}: il risultato è ${RESULT}" }
